import Button from '@material-ui/core/Button';
import Tooltip from '@material-ui/core/Tooltip';
import makeStyles from '@material-ui/core/styles/makeStyles';
import { useState } from 'react';

import { minimizeAddress } from '../../../libs/utils';

interface Props {
  address: string | null;
  loading: boolean;
  connect: () => void;
}

const useStyles = makeStyles(() => ({
  loggedInBtn: {
    borderRadius: 8,
    color: 'white',
    borderColor: 'white !important',
    '& img': {
      marginLeft: 8,
    },
  },
}));

export const HeaderUserButton = ({ address, loading, connect }: Props) => {
  const styles = useStyles();
  const [copied, setCopied] = useState(false);

  const copy = () => {
    if (copied) {
      return;
    }
    navigator.clipboard.writeText(address as string);
    setCopied(true);
    setTimeout(() => setCopied(false), 1000);
  };

  return address ? (
    <Tooltip title={copied ? 'Copied' : 'Copy'}>
      <Button className={styles.loggedInBtn} variant="outlined" disabled={loading} onClick={copy}>
        {minimizeAddress(address)}
        <img height={13} src="/copy.png" alt="" />
      </Button>
    </Tooltip>
  ) : (
    <Button
      style={{ marginLeft: 24, width: 240 }}
      className="gradient-button"
      disabled={loading}
      variant="outlined"
      onClick={() => connect()}
    >
      Connect Wallet
    </Button>
  );
};

export default HeaderUserButton;
