"use strict";
/* global window */

const Web3 = window.Web3;

class UnitConverter {
  static ETH(value) {
    return Web3.utils.toWei(value, 'ether');
  }

  static Token(value, decimals) {
    const decimalValue = Web3.utils.toBN(decimals);
    return Web3.utils.toHex(Web3.utils.toBN(value).mul(Web3.utils.toBN(10).pow(decimalValue)));
  }

}

module.exports = UnitConverter;